#!/usr/bin/env python
import libbaltcalc

#used to get linear adress point number in decimal (for example for fiding an adress in the rom)
def numstruct(code):
	strut1=(libbaltcalc.BTTODEC(code))
	strut2=(strut1 + 365)
	#print strut2
	return strut2

#used by the buzzer (do not use for 6 trit numtructs. use normal numstruct for that!)
def buzznumstruct5(code):
	strut1=(libbaltcalc.BTTODEC(code))
	strut2=(strut1 + 122)
	#print strut2
	return strut2

#more intelegent truncation function (used in the math functions for example)
def trunkto6(code):
	codecnt=0
	for fel in code:
		codecnt +=1
	if codecnt<6:
		if codecnt==5:
			return("0" + code)
		if codecnt==4:
			return("00" + code)
		if codecnt==3:
			return("000" + code)
		if codecnt==2:
			return("0000" + code)
		if codecnt==1:
			return("00000" + code)
	code=libbaltcalc.BTINVERT(code)
	return((code[5]) + (code[4]) + (code[3]) + (code[2]) + (code[1]) + (code[0]))

